import { Grid, TextField, Typography, Button, Input } from '@mui/material'; 
import { useNavigate } from 'react-router-dom';
import { useState } from 'react'

function Login () {
    const [visibleAthleteName, setVisibleAthleteName] = useState("");
    const [athleteName, setAthleteName] = useState("");
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [athleteError, setAthleteError] = useState('');

    async function onButtonClick (){
        setAthleteError('')


        if ('' == athleteName) {
            setAthleteError('Please enter an athlete id.')
            return
        }
        setIsAuthenticated(true)
        setVisibleAthleteName(athleteName)

        useEffect(() => {
            fetch("https://jokes-by-api-ninjas.p.rapidapi.com/v1/jokes", {
              method: "GET",
              headers: {
                "X-RapidAPI-Key": "your-api-key",
                "X-RapidAPI-Host": "jokes-by-api-ninjas.p.rapidapi.com",
              },
            })
              .then((response) => response.json())
              .then((data) => {
                setJoke(data[0].joke);
                console.log(data);
              })
              .catch((error) => console.log(error));
          }, []);

        // const text = await fetch('http://localhost:7071/api/get_athlete?name=Eli')
        //                         .then(response => console.log(response))
                                // .then(response => response.json())
                                // .then(json => setIsAuthenticated)
                                // .then(error => console.error(error))
        //document.querySelector('#name').textContent = text;

        // // authentication call will be made here.
        // const fetchPost = async () => {
        //     try {
        //        const response = await fetch(
        //           '/api/get_athlete', {
        //           method: 'POST',
        //           body: JSON.stringify({"name": athleteName}),
        //           headers: {
        //             'Content-type': 'application/json; charset=UTF-8',
        //           }
        //        });
        //        const data = await response.json();
        //        console.log(data);
        //     } catch (error) {
        //        console.log(error);
        //     }
        // };
    }

    function handleAthleteInput(e) {
        setAthleteName(e.target.value)
    }
    return (
        <>
        <Grid item>
            <TextField label="Athlete ID" onChange={handleAthleteInput}></TextField>
            <Button onClick={onButtonClick}>Login</Button>
            <div>{isAuthenticated ? <div>{visibleAthleteName} has been logged in!</div>:<></>} </div>
        </Grid>
        </>
    )
}

export const LoginPage = () => {
    
    const navigate = useNavigate()
    const routeChangeForgotId = () => {
        let path = "/forgotId";
        navigate(path)
    }
    const routeChangeNewAthlete = () => {
        let path = "/newAthlete";
        navigate(path)
    }
    return <Grid container sx={{
        justifyContent: "center"
        }}>
        <Grid item>
            <Typography variant="h1">
            Welcome to Legacy Boxing!
            </Typography>
        </Grid>
        <Grid container item xs={12} columnSpacing={3} rowSpacing={3} sx={{
            justifyContent: "center",
            paddingTop: "1rem",
            alignItems: "center"
        }}>
            {/* <Grid item>
            <TextField label="Athlete ID" required></TextField>
            </Grid> */}
            <Grid item>
            <Login />
            </Grid>
        </Grid>
        <Grid container item xs={12} columnSpacing={3} rowSpacing={3} sx={{
            justifyContent: "center",
            paddingTop: "1rem",
            alignItems: "center"
        }}>
            <Grid item>
            <Button onClick={ () => routeChangeForgotId() }>Forgot ID</Button>
            </Grid>
        </Grid>
        <Grid container item xs={12} columnSpacing={3} rowSpacing={3} sx={{
            justifyContent: "center",
            paddingTop: "1rem",
            alignItems: "center"
        }}>
            <Grid item>
            <Button onClick={ () => routeChangeNewAthlete() }>New Athlete</Button>
            </Grid>
        </Grid>
    </Grid>  
}
