import { Grid, TextField, Typography, Button } from '@mui/material'; 
import { useNavigate } from 'react-router-dom';
    import { useState } from 'react'

function LoginUser() {
    const [athleteName, setAthleteName] = useState('');
    const [isAuthenticated, setAuthentication] = useState(false)
    const [emailError, setEmailError] = useState('')
    const [passwordError, setPasswordError] = useState('')
    function ValidateAthlete() {
        setAuthentication = true
        setAthleteName = "Eli"
    }
    return (
        <>
            <Grid item>
            <TextField 
                label="Athlete ID" 
                required
                value={athleteName}
            />
            <Button onClick={ValidateAthlete}>Login</Button>
            <div>
                {isAuthenticated ? <div>Welcome {athleteName}</div>: <div />}
            </div>
            </Grid>
        </>
    )
}

export const LoginPage = () => {
    
    const navigate = useNavigate()
    const routeChangeForgotId = () => {
        let path = "/forgotId";
        navigate(path)
    }
    const routeChangeNewAthlete = () => {
        let path = "/newAthlete";
        navigate(path)
    }
    return <Grid container sx={{
        justifyContent: "center"
        }}>
        <Grid item>
            <Typography variant="h1">
            Welcome to Legacy Boxing!
            </Typography>
        </Grid>
        <Grid container item xs={12} columnSpacing={3} rowSpacing={3} sx={{
            justifyContent: "center",
            paddingTop: "1rem",
            alignItems: "center"
        }}>
            {/* <Grid item>
            <TextField label="Athlete ID" required></TextField>
            </Grid> */}
            <Grid item>
            <LoginUser />
            </Grid>
        </Grid>
        <Grid container item xs={12} columnSpacing={3} rowSpacing={3} sx={{
            justifyContent: "center",
            paddingTop: "1rem",
            alignItems: "center"
        }}>
            <Grid item>
            <Button onClick={ () => routeChangeForgotId() }>Forgot ID</Button>
            </Grid>
        </Grid>
        <Grid container item xs={12} columnSpacing={3} rowSpacing={3} sx={{
            justifyContent: "center",
            paddingTop: "1rem",
            alignItems: "center"
        }}>
            <Grid item>
            <Button onClick={ () => routeChangeNewAthlete() }>New Athlete</Button>
            </Grid>
        </Grid>
    </Grid>  
}
